var GetPatientDetail = Class.create();
GetPatientDetail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getPatient: function() {
    	var userRecord = new GlideRecord("x_423236_health_ca_patient");
    	userRecord.get(this.getParameter('user_name'));

		 var obj = {};
         obj.fn = userRecord.getValue('first_name');
         obj.ln = userRecord.getValue('last_name');


         var json = new JSON();
         var data = json.encode(obj);//JSON formatted string                       


    	return data;
	},


    type: 'GetPatientDetail'
});
